import React from 'react';
import BackButton from './BackButton';
import G from '../images/g.jpg';
import mansion from '../images/mansion.jpg';
import noordin from '../images/noordin.jpg';


const hotels = [
  {
    id: 1,
    name: "G Hotel Kelawai",
    description: "豪华海景酒店，提供一流的服务和设施。",
    image: G,
    price: "RM 500起/晚",
    rating: 4.9,
    amenities: ["游泳池", "Spa", "健身中心", "免费早餐"]
  },
  {
    id: 2,
    name: "Noordin Mews",
    description: "位于乔治市古城区的精品文化酒店，古色古香。",
    image: noordin,
    price: "RM 600起/晚",
    rating: 4.7,
    amenities: ["免费WiFi", "文化导览", "传统建筑"]
  },
  {
    id: 3,
    name: "Cheong Fatt Tze, The Blue Mansion",
    description: "经济实惠的住宿选择，适合背包客。",
    image: mansion,
    price: "RM 50起/晚",
    rating: 4.5,
    amenities: [ "行李寄存", "旅游信息"]
  }
];

const Hotels = () => {
  return (
    <div className="hotels-page">
      <header className="page-header">
        <h1>槟城住宿</h1>
        <p className="subtitle">为您的槟城之旅找到完美住处</p>
      </header>

      <div className="grid">
        {hotels.map(hotel => (
          <div key={hotel.id} className="card">
            <img
              src={hotel.image}
              alt={hotel.name}
              className="card-image"
              onError={(e) => {
                e.target.src = 'https://via.placeholder.com/400x300?text=酒店图片';
              }}
            />
            <div className="card-content">
              <h2 className="card-title">{hotel.name}</h2>
              <p className="card-text">{hotel.description}</p>
              <div className="amenities">
                <h3>设施服务</h3>
                <ul className="amenities-list">
                  {hotel.amenities.map((item, index) => (
                    <li key={index}>{item}</li>
                  ))}
                </ul>
              </div>
              <div className="card-footer">
                <span className="price">{hotel.price}</span>
                <span className="rating">★ {hotel.rating}</span>
              </div>
            </div>
          </div>
        ))}
      </div>

      <BackButton />
    </div>
  );
};

export default Hotels;
