import React from 'react';
import BackButton from './BackButton';
import 姓氏桥 from '../images/姓氏桥.jpg';
import 极乐寺 from '../images/极乐寺.jpg';
import 槟城街头艺术 from '../images/槟城街头艺术.jpg';

const spots = [
  {
    id: 1,
    name: "姓氏桥",
    description: "历史悠久的水上木屋聚落，展现早期华人移民生活场景。",
    image: 姓氏桥,
    price: "免费",
    rating: 4.6,
    openHours: "全天开放"
  },
  {
    id: 2,
    name: "极乐寺",
    description: "东南亚最大的佛教寺庙之一，建筑宏伟，景色壮观。",
    image: 极乐寺,
    price: "RM 2",
    rating: 4.8,
    openHours: "9:00 - 17:30"
  },
  {
    id: 3,
    name: "槟城街头艺术",
    description: "著名的街头壁画，展现槟城的文化特色和艺术魅力。",
    image: 槟城街头艺术,
    price: "免费",
    rating: 4.7,
    openHours: "全天可观赏"
  }
];

const TouristSpots = () => {
  return (
    <div className="spots-page">
      <header className="page-header">
        <h1>槟城景点</h1>
        <p className="subtitle">探索世界文化遗产名城的独特魅力</p>
      </header>

      <div className="grid">
        {spots.map(spot => (
          <div key={spot.id} className="card">
            <img
              src={spot.image}
              alt={spot.name}
              className="card-image"
              onError={(e) => {
                e.target.src = 'https://via.placeholder.com/400x300?text=景点图片';
              }}
            />
            <div className="card-content">
              <h2 className="card-title">{spot.name}</h2>
              <p className="card-text">{spot.description}</p>
              <div className="spot-details">
                <p><strong>开放时间：</strong>{spot.openHours}</p>
                <p><strong>门票：</strong>{spot.price}</p>
              </div>
              <div className="card-footer">
                <span className="rating">★ {spot.rating}</span>
              </div>
            </div>
          </div>
        ))}
      </div>

      <BackButton />
    </div>
  );
};

export default TouristSpots;
