import React from 'react';
import { Link } from 'react-router-dom';
import 槟城风光 from '../images/姓氏桥.jpg';
import 美食天堂 from '../images/椰浆饭.png';
import 特色住宿 from '../images/mansion.jpg';

const highlights = [
  {
    id: 1,
    title: "热门景点",
    description: "探索槟城乔治市的世界文化遗产建筑群",
    image: 槟城风光,
    link: "/tourist-spots"
  },
  {
    id: 2,
    title: "美食天堂",
    description: "品尝槟城独特的娘惹美食和街边小吃",
    image: 美食天堂,
    link: "/food"
  },
  {
    id: 3,
    title: "特色住宿",
    description: "体验古迹酒店到现代化度假村的多样选择",
    image: 特色住宿,
    link: "/hotels"
  }
];

const Home = () => {
  return (
    <div className="home-page">
      <section className="hero" style={{
        backgroundImage: "url('/images/penang-hero.jpg')",
        backgroundSize: "cover",
        backgroundPosition: "center",
        height: "500px",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        color: "#fff",
        textShadow: "2px 2px 4px rgba(0,0,0,0.5)"
      }}>
        <h1>欢迎来到槟城</h1>
        <p>探索马来西亚最迷人的文化之都</p>
      </section>

      <section className="highlights">
        <h2 className="section-title">精彩推荐</h2>
        <div className="grid">
          {highlights.map(item => (
            <Link to={item.link} key={item.id} className="highlight-card card">
              <img
                src={item.image}
                alt={item.title}
                className="card-image"
                onError={(e) => {
                  e.target.src = 'https://via.placeholder.com/400x300?text=槟城风光';
                }}
              />
              <div className="card-content">
                <h3 className="card-title">{item.title}</h3>
                <p className="card-text">{item.description}</p>
              </div>
            </Link>
          ))}
        </div>
      </section>

      <section className="quick-info">
        <div className="info-grid">
          <div className="info-item">
            <h3>最佳旅游季节</h3>
            <p>11月至次年1月是槟城的最佳旅游季节</p>
          </div>
          <div className="info-item">
            <h3>交通方式</h3>
            <p>可选择出租车、公交车或步行探索城市</p>
          </div>
          <div className="info-item">
            <h3>当地货币</h3>
            <p>马来西亚林吉特（MYR）</p>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
