import React from 'react';
import BackButton from './BackButton';
import laksa from '../images/laksa.png';
import 粿条 from '../images/炒粿条.png';
import 娘惹 from '../images/娘惹.png';
import foodbg from '../images/food.jpg';

const foodItems = [
  {
    id: 1,
    name: "槟城叻沙",
    description: "浓郁的椰浆汤底配以虾肉、鱼丸和米粉，是槟城最著名的美食之一。",
    image: laksa, // 需要替换为实际图片URL
    price: "RM 8-12",
    rating: 4.8
  },
  {
    id: 2,
    name: "炒粿条",
    description: "新鲜的河粉配以虾仁、豆芽和韭黄快速翻炒，香气四溢的街头美食。",
    image: 粿条, // 需要替换为实际图片URL
    price: "RM 7-10",
    rating: 4.7
  },
  {
    id: 3,
    name: "娘惹糕点",
    description: "结合马来与华人文化的特色糕点，色彩缤纷，口感细腻。",
    image: 娘惹, // 需要替换为实际图片URL
    price: "RM 1-3/件",
    rating: 4.5
  }
];

const Food = () => {
  return (
    <div className="food-page">
      <div 
        className="page-background" 
        style={{
          backgroundImage: `url(${foodbg})`,
        }}
      />
      <header className="page-header">
        <h1>槟城美食</h1>
        <p className="subtitle">探索槟城独特的美食文化</p>
      </header>

      <div className="grid">
        {foodItems.map(item => (
          <div key={item.id} className="card">
            <img 
              src={item.image} 
              alt={item.name}
              className="card-image"
              onError={(e) => {
                e.target.src = 'https://via.placeholder.com/400x300?text=美食图片';
              }}
            />
            <div className="card-content">
              <h2 className="card-title">{item.name}</h2>
              <p className="card-text">{item.description}</p>
              <div className="card-footer">
                <span className="price">{item.price}</span>
                <span className="rating">★ {item.rating}</span>
              </div>
            </div>
          </div>
        ))}
      </div>

      <BackButton />
    </div>
  );
};

export default Food;
